import React, { Component } from 'react';
import {Button, FormGroup, FormControl, Form, Container} from "react-bootstrap";
import { Redirect } from 'react-router-dom'
import { withRouter } from 'react-router-dom'
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Row, Col, ListGroup } from 'react-bootstrap';
import Avatar from 'react-toolbox/lib/avatar';

const initialData = {
    "names": [
        {"id": 1, "name": "Airtel", "funcValue": "Piyush Aggarwal", "portvalue": "403"}, 
        {"id": 2, "name": "Vodafone", "funcValue": "Abhaas Aggarwal", "portvalue": "403"}, 
        {"id": 3, "name": "Idea", "funcValue": "Abhinav Aggarwal", "portvalue": "403"}
    ]}

const namesData=["Piyush Aggarwal","Abhaas Aggarwal","Abhinav Aggarwal","Sunita Aggarwal","Bhavya Aggarwal","Nimesh Johri"];
const portNumber=["403","200","100","560","120","282","328","182"]

const GithubIcon = () => (
    <svg viewBox="0 0 284 277">
    <g><path d="M141.888675,0.0234927555 C63.5359948,0.0234927555 0,63.5477395 0,141.912168 C0,204.6023 40.6554239,257.788232 97.0321356,276.549924 C104.12328,277.86336 106.726656,273.471926 106.726656,269.724287 C106.726656,266.340838 106.595077,255.16371 106.533987,243.307542 C67.0604204,251.890693 58.7310279,226.56652 58.7310279,226.56652 C52.2766299,210.166193 42.9768456,205.805304 42.9768456,205.805304 C30.1032937,196.998939 43.9472374,197.17986 43.9472374,197.17986 C58.1953153,198.180797 65.6976425,211.801527 65.6976425,211.801527 C78.35268,233.493192 98.8906827,227.222064 106.987463,223.596605 C108.260955,214.426049 111.938106,208.166669 115.995895,204.623447 C84.4804813,201.035582 51.3508808,188.869264 51.3508808,134.501475 C51.3508808,119.01045 56.8936274,106.353063 65.9701981,96.4165325 C64.4969882,92.842765 59.6403297,78.411417 67.3447241,58.8673023 C67.3447241,58.8673023 79.2596322,55.0538738 106.374213,73.4114319 C117.692318,70.2676443 129.83044,68.6910512 141.888675,68.63701 C153.94691,68.6910512 166.09443,70.2676443 177.433682,73.4114319 C204.515368,55.0538738 216.413829,58.8673023 216.413829,58.8673023 C224.13702,78.411417 219.278012,92.842765 217.804802,96.4165325 C226.902519,106.353063 232.407672,119.01045 232.407672,134.501475 C232.407672,188.998493 199.214632,200.997988 167.619331,204.510665 C172.708602,208.913848 177.243363,217.54869 177.243363,230.786433 C177.243363,249.771339 177.078889,265.050898 177.078889,269.724287 C177.078889,273.500121 179.632923,277.92445 186.825101,276.531127 C243.171268,257.748288 283.775,204.581154 283.775,141.912168 C283.775,63.5477395 220.248404,0.0234927555 141.888675,0.0234927555" /></g>
    </svg>
);
const getListStyle = isDraggingOver => ({
    background: isDraggingOver ? "lightblue" : "lightgrey",
    padding: grid,
    width: 400
});

const grid = 8;

const getItemStyleOne = (isDragging, draggableStyle) => ({
    // some basic styles to make the items look a bit nicer
    userSelect: "none",
    padding: grid * 2,
    margin: `0 0 ${grid}px 0`,
    // change background colour if dragging
    background: isDragging ? "lightgreen" : "grey",
    // styles we need to apply on draggables
    ...draggableStyle
});

const getItemStyleTwo = (isDragging, draggableStyle) => ({
    // some basic styles to make the items look a bit nicer
    userSelect: "none",
    padding: grid * 2,
    margin: `0 0 ${grid}px 0`,
    // change background colour if dragging
    background: isDragging ? "lightcoral" : "lime",
    // styles we need to apply on draggables
    ...draggableStyle
});

const getItemStyleThree = (isDragging, draggableStyle) => ({
    // some basic styles to make the items look a bit nicer
    userSelect: "none",
    padding: grid * 2,
    margin: `0 0 ${grid}px 0`,
    // change background colour if dragging
    background: isDragging ? "lightgreen" : "blanchedalmond",
    // styles we need to apply on draggables
    ...draggableStyle
});

const reorder = (list, startIndex, endIndex) => {
    console.log(list)
    const result = Array.from(list);

    const [removed] = result.splice(startIndex, 1);
    console.log(removed);

    result.splice(endIndex, 0, removed);

    return result;
  };



class Boarding extends Component {
    constructor(props) {
      super(props);
      this.onDragEnd = this.onDragEnd.bind(this);
      this.state = {
        items: initialData,
        namesData: namesData,
        portNumber:portNumber
      };
    }

    onDragEnd(result) {
        // dropped outside the list
        console.log(result);
        if (!result.destination) {
          return;
        }

        if(result.destination.droppableId=="droppableOne"){
            console.log("droppableOnne");
        }

        if(result.destination.droppableId=="droppableTwo"){
            console.log("droppableTwo");
        }

        if(result.destination.droppableId=="droppableThree"){
            console.log("droppableThree");
        }

        // const items = reorder(
        //   this.state.items,
        //   result.source.index,
        //   result.destination.index
        // );

        // this.setState({
        //   items
        // });
    }

    render() {
        return (
            <Row> 
                <Col xs={4}>
                    <Button style={{margin:"10px",width:"50%"}} variant="success">Providers</Button>
                    <DragDropContext style={{margin:"10px"}} onDragEnd={this.onDragEnd}>
                        <Droppable droppableId="droppableOne">
                            {(provided, snapshot)=>(
                                <div {...provided.droppableProps} ref={provided.innerRef} style={getListStyle(snapshot.isDraggingOver)}>
                                    {this.state.items.names.map((item, index) => (
                                        <Draggable key={item.id} draggableId={item.name} index={index}>
                                            {(provided, snapshot) => (
                                                <div
                                                ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                style={getItemStyleOne(
                                                    snapshot.isDragging,
                                                    provided.draggableProps.style
                                                    )}
                                                >
                                                {item.name}
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                </div>
                            )}
                        </Droppable>
                    </DragDropContext>
                </Col>

                <Col xs={4}>
                    <Button style={{margin:"10px",width:"50%"}} variant="success">Names</Button>
                    <DragDropContext onDragEnd={this.onDragEnd}>
                        <Droppable droppableId="droppableTwo">
                            {(provided, snapshot)=>(
                                <div {...provided.droppableProps} ref={provided.innerRef} style={getListStyle(snapshot.isDraggingOver)}>
                                    {this.state.namesData.map((item, index) => (
                                        <Draggable key={item} draggableId={item} index={index}>
                                            {(provided, snapshot) => (
                                                <div
                                                ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                style={getItemStyleTwo(
                                                    snapshot.isDragging,
                                                    provided.draggableProps.style
                                                    )}
                                                >
                                                <span>
                                                {item}
                                                </span>


                                               </div>
                                            )}
                                        </Draggable>
                                    ))}
                                </div>
                            )}
                        </Droppable>
                    </DragDropContext>
                </Col>

                <Col xs={4}>
                    <Button style={{margin:"10px",width:"50%"}} variant="success">Port Numbers</Button>
                    <DragDropContext onDragEnd={this.onDragEnd}>
                        <Droppable droppableId="droppableThree">
                            {(provided, snapshot)=>(
                                <div {...provided.droppableProps} ref={provided.innerRef} style={getListStyle(snapshot.isDraggingOver)}>
                                    {this.state.portNumber.map((item, index) => (
                                        <Draggable key={item} draggableId={item} index={index}>
                                            {(provided, snapshot) => (
                                                <div
                                                ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                style={getItemStyleThree(
                                                    snapshot.isDragging,
                                                    provided.draggableProps.style
                                                    )}
                                                >
                                                 {item}
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                </div>
                            )}
                        </Droppable>
                    </DragDropContext>
                </Col>
            </Row>
        )
    }

    onDrop(data) {
        console.log(this)
    }
  }

export default (withRouter)(Boarding)
